'use client';

import * as React from 'react';

import type { RenderElementProps, TElement, Value } from 'platejs';

import {
  BasicBlocksPlugin,
  BasicMarksPlugin,
} from '@platejs/basic-nodes/react';
import {
  ContentVisibilityChunk,
  Editable,
  Plate,
  Slate,
  usePlateEditor,
  withReact,
} from 'platejs/react';
import { createEditor } from 'slate';

import { createHugeDocumentValue } from '@/registry/examples/values/huge-document-value';
import { Editor, EditorContainer } from '@/registry/ui/editor';

const value = createHugeDocumentValue();

function WithPlate() {
  const editor = usePlateEditor({
    nodeId: false,
    plugins: [BasicBlocksPlugin, BasicMarksPlugin],
    value,
  });

  return (
    <Plate editor={editor}>
      <EditorContainer>
        <Editor spellCheck={false} />
      </EditorContainer>
    </Plate>
  );
}

function Element({ attributes, children, element }: RenderElementProps) {
  switch ((element as TElement).type) {
    case 'h1': {
      return <h1 {...attributes}>{children}</h1>;
    }
    default: {
      return <p {...attributes}>{children}</p>;
    }
  }
}

function WithoutPlate() {
  const [initialValue, setValue] = React.useState(value);
  const renderElement = React.useCallback((p: any) => <Element {...p} />, []);
  const editor = React.useMemo(() => {
    const e = withReact(createEditor());
    e.getChunkSize = (node) => (node === e ? 1000 : null);
    return e;
  }, []);
  const onChange = React.useCallback(
    (newValue: Value) => setValue(newValue),
    []
  );

  return (
    <Slate
      onChange={onChange as any}
      editor={editor}
      initialValue={initialValue}
    >
      <Editable
        renderChunk={ContentVisibilityChunk}
        renderElement={renderElement}
        spellCheck={false}
      />
    </Slate>
  );
}

export default function HundredsBlocksDemo() {
  return (
    <div className="flex">
      <div className="w-1/2 p-4">
        <div className="mb-4 font-bold text-lg">Plate</div>
        <WithPlate />
      </div>
      <div className="w-1/2 p-4">
        <div className="mb-4 font-bold text-lg">Slate</div>
        <WithoutPlate />
      </div>
    </div>
  );
}

/** @jsxRuntime classic */
/** @jsx jsx */
import type { Descendant, Value } from 'platejs';

import { jsx } from '@platejs/test-utils';

jsx;

const HEADINGS = 300;
const PARAGRAPHS = 10;

export const createHugeDocumentValue = () => {
  const hugeDocument: Descendant[] = [];

  for (let h = 0; h < HEADINGS; h++) {
    hugeDocument.push(
      (
        <hh1>Do voluptate enim commodo quis ad aliqua dolore enim eu nisi.</hh1>
      ) as any
    );

    for (let p = 0; p < PARAGRAPHS; p++) {
      hugeDocument.push(
        (
          <hp>
            Ex est consequat anim ad deserunt sint. Ea excepteur consequat amet
            amet excepteur culpa nulla. Voluptate exercitation pariatur enim.
            Excepteur ea nulla nostrud est ex sunt anim. Sunt laborum et et ea
            aliquip excepteur sint nulla amet. Sunt sit cillum amet. Anim esse
            ut irure ipsum irure proident consectetur eu velit esse. Laborum
            minim laborum laborum sunt eiusmod aliqua fugiat adipisicing. Cillum
            aliqua exercitation ex aliquip aliquip amet aliquip est eiusmod
            tempor pariatur veniam adipisicing ad. Officia sunt ipsum
            adipisicing eu quis laborum do cupidatat officia dolor.
          </hp>
        ) as any
      );
    }
  }

  return hugeDocument as Value;
};
