'use client';

import React from 'react';

import { cn } from '@udecode/cn';
import {
  type TCloudAttachmentElement,
  useCloudAttachmentElementState,
} from '@udecode/plate-cloud';
import { PlateElement, type PlateElementProps } from '@udecode/plate-common';

import { Icons } from '@/components/icons';

import { StatusBar } from './cloud-status-bar';

export interface CloudAttachmentElementProps
  extends PlateElementProps<TCloudAttachmentElement> {}

export function CloudAttachmentElement({
  className,
  ...props
}: CloudAttachmentElementProps) {
  const { children, element } = props;

  const { focused, selected, upload } = useCloudAttachmentElementState({
    element,
  });

  return (
    <PlateElement
      className={cn(
        'relative my-4 flex h-10 max-w-sm items-center gap-2 rounded-lg border border-border bg-background p-4',
        focused && selected && 'border-blue-400 shadow-[0_0_1px_3px_#60a5fa]',
        className
      )}
      draggable
      {...props}
    >
      <div className="shrink-0 text-muted-foreground" contentEditable={false}>
        <Icons.attachment height={24} width={24} />
      </div>
      <div className="grow" contentEditable={false}>
        <div className="text-base">{element.filename}</div>
        <StatusBar upload={upload}>
          <div className="text-sm text-muted-foreground">
            {element.bytes} bytes
          </div>
        </StatusBar>
      </div>
      <div
        className="ml-4 size-8 shrink-0 duration-200"
        contentEditable={false}
      >
        {upload.status === 'success' && (
          <a href={element.url} rel="noreferrer" target="_blank">
            <Icons.downloadCloud
              className="cursor-pointer text-muted-foreground hover:text-foreground"
              height={24}
              width={24}
            />
          </a>
        )}
      </div>
      {children}
    </PlateElement>
  );
}

'use client';

import React from 'react';

import { cn } from '@udecode/cn';
import {
  type TCloudImageElement,
  useCloudImageElementState,
} from '@udecode/plate-cloud';
import { PlateElement, type PlateElementProps } from '@udecode/plate-common';

import { ResizeControls } from './cloud-resize-controls';
import { StatusBar } from './cloud-status-bar';

export interface CloudImageElementProps
  extends PlateElementProps<TCloudImageElement> {}

export function CloudImageElement({
  className,
  ...props
}: CloudImageElementProps) {
  const { children, element } = props;

  const { focused, selected, setSize, size, src, srcSet, upload } =
    useCloudImageElementState({ element });

  return (
    <PlateElement
      className={cn('relative my-4', className)}
      draggable
      {...props}
    >
      <span
        contentEditable={false}
        style={{
          display: 'inline-block',
          /**
           * NOTE: This code pretty much needs to be this way or things stop
           * working so this cannot be overrided in the `.styles.ts` file.
           */
          position: 'relative',
          /** Disable user select. We use our own selection display. */
          userSelect: 'none',
          /**
           * This is required so that we don't get an extra gap at the bottom.
           * When display is 'inline-block' we get some extra space at the
           * bottom for the descenders because the content is expected to
           * co-exist with text.
           *
           * Setting vertical-align to top, bottom or middle fixes this because
           * it is no longer baseline which causes the issue.
           *
           * This is usually an issue with 'img' but also affects this scenario.
           *
           * https://stackoverflow.com/questions/5804256/image-inside-div-has-extra-space-below-the-image
           *
           * Also, make sure that <img> on the inside is display: 'block'.
           */
          verticalAlign: 'top',
        }}
      >
        {src === '' ? (
          <div
            className={cn(
              'block rounded-lg',
              focused && selected && 'shadow-[0_0_1px_3px_#60a5fa]'
            )}
            style={{
              background: '#e0e0e0',
              height: size.height,
              width: size.width,
            }}
          />
        ) : (
          <img
            alt=""
            className={cn(
              'block rounded-lg',
              focused && selected && 'shadow-[0_0_1px_3px_#60a5fa]'
            )}
            height={size.height}
            src={src}
            srcSet={srcSet}
            width={size.width}
          />
        )}
        <div className="absolute inset-x-2 top-1/2 -mt-2">
          <StatusBar upload={upload} />
        </div>
        {selected && focused && (
          <ResizeControls element={element} setSize={setSize} size={size} />
        )}
      </span>
      {children}
    </PlateElement>
  );
}

'use client';

import React, {
  type Dispatch,
  type SetStateAction,
  useCallback,
  useRef,
  useState,
} from 'react';

import type {
  PlateCloudEditor,
  PlateCloudImageEditor,
  TCloudImageElement,
} from '@udecode/plate-cloud';

import { resizeInWidth } from '@portive/client';
import { findNodePath, setNodes, useEditorRef } from '@udecode/plate-common';

type ImageSize = { height: number; width: number };

type SetImageSize = Dispatch<SetStateAction<ImageSize>>;

/** The resize label that shows the width/height of the image */
function ResizeLabel({ size }: { size: { height: number; width: number } }) {
  const isBelow = size.width < 100 || size.height < 100;
  const bottom = isBelow ? -24 : 4;

  return (
    <div
      style={{
        background: '#404040',
        borderRadius: 3,
        bottom,
        boxShadow: '0px 0px 2px 1px rgba(255, 255, 255, 0.5)',
        color: 'white',
        font: '10px/20px sans-serif',
        left: 4,
        minWidth: 50,
        padding: '0 7px',
        position: 'absolute',
        textAlign: 'center',
        transition: 'bottom 250ms',
        zIndex: 100,
      }}
    >
      {size.width} &times; {size.height}
    </div>
  );
}

/** The little divets on the resize handle bar. */
const barStyle = {
  background: 'rgba(255,255,255,0.75)',
  height: 16,
  position: 'absolute',
  top: 8,
  width: 1,
} as const;

/** The handle used to drag resize an image */
function ResizeHandles({
  onMouseDown,
}: {
  onMouseDown: React.MouseEventHandler;
}) {
  return (
    <>
      {/* Invisible Handle */}
      <div
        onMouseDown={onMouseDown}
        style={{
          background: 'rgba(127,127,127,0.01)',
          bottom: 0,
          cursor: 'ew-resize',
          position: 'absolute',
          right: -8,
          top: 0,
          width: 16,
        }}
      >
        {/* Visible Handle */}
        <div
          style={{
            background: 'DodgerBlue',
            borderRadius: 4,
            height: 32,
            left: 0,
            marginTop: -16,
            position: 'absolute',
            top: '50%',
            width: 16,
          }}
        >
          <div style={{ ...barStyle, left: 3.5 }} />
          <div style={{ ...barStyle, left: 7.5 }} />
          <div style={{ ...barStyle, left: 11.5 }} />
        </div>
      </div>
    </>
  );
}

export function ResizeControls({
  element,
  setSize,
  size,
}: {
  element: TCloudImageElement;
  setSize: SetImageSize;
  size: ImageSize;
}) {
  const editor = useEditorRef() as PlateCloudEditor & PlateCloudImageEditor;
  const [isResizing, setIsResizing] = useState(false);

  const { maxResizeWidth, minResizeWidth } = editor.cloudImage;

  const currentSizeRef = useRef<{ height: number; width: number }>();

  const onMouseDown = useCallback(
    (mouseDownEvent: React.MouseEvent) => {
      setIsResizing(true);
      const startX = mouseDownEvent.clientX;
      const startWidth = size.width;
      const minWidth = minResizeWidth;
      const maxWidth = Math.min(element.maxWidth, maxResizeWidth);

      /**
       * Handle resize dragging through an event handler on mouseMove on the
       * document.
       */
      function onDocumentMouseMove(mouseMoveEvent: MouseEvent) {
        mouseMoveEvent.preventDefault();
        mouseMoveEvent.stopPropagation();
        /** Calculate the proposed width based on drag position */
        const proposedWidth = startWidth + mouseMoveEvent.clientX - startX;

        /** Constrain the proposed with between min, max and original width */
        const nextWidth = Math.min(maxWidth, Math.max(minWidth, proposedWidth));

        const currentSize = resizeInWidth(
          { height: element.maxHeight, width: element.maxWidth },
          nextWidth
        );

        currentSizeRef.current = currentSize;
        setSize(currentSize);
      }

      const originalCursor = document.body.style.cursor;

      /** When the user releases the mouse, remove all the event handlers */
      function onDocumentMouseUp() {
        setIsResizing(false);
        document.removeEventListener('mousemove', onDocumentMouseMove);
        document.removeEventListener('mouseup', onDocumentMouseUp);
        document.body.style.cursor = originalCursor;

        const at = findNodePath(editor, element);

        if (!currentSizeRef.current) return;

        setNodes<TCloudImageElement>(editor, currentSizeRef.current, { at });
      }

      /** Attach document event listeners */
      document.addEventListener('mousemove', onDocumentMouseMove);
      document.addEventListener('mouseup', onDocumentMouseUp);

      /**
       * While dragging, we want the cursor to be `ew-resize` (left-right arrow)
       * even if the cursor happens to not be exactly on the handle at the
       * moment due to a delay in the cursor moving to a location and the image
       * resizing to it.
       *
       * Also, image has max width/height and the cursor can fall outside of it.
       */
      document.body.style.cursor = 'ew-resize';
    },
    [size.width, minResizeWidth, element, maxResizeWidth, setSize, editor]
  );

  if (element.width < minResizeWidth) return null;

  return (
    <>
      {isResizing ? <ResizeLabel size={size} /> : null}
      <ResizeHandles onMouseDown={onMouseDown} />
    </>
  );
}

'use client';

import React, { type HTMLAttributes, useEffect, useRef, useState } from 'react';

import type { Upload, UploadProgress } from '@udecode/plate-cloud';

import { cn } from '@udecode/cn';

export function ProgressBar({
  className,
  upload,
  ...props
}: {
  upload: UploadProgress;
} & HTMLAttributes<HTMLDivElement>) {
  const [width, setWidth] = useState<null | number>(null);
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (ref.current) setWidth(ref.current.offsetWidth);
  }, []);

  /**
   * This formula looks a little funny because we want the `0` value of the
   * progress bar to have a width that is still the height of the progress bar.
   *
   * This is for a few reasons:
   *
   * 1. We want the zero point to start with the progress bar being a circle
   * 2. If we want rounded edges, if the width is shorter than the height, we get
   *    an oval instead of a circle
   * 3. The halfway point looks visually wrong because of the circle progress bar
   *    when it is technically at the halfway point.
   */
  const progressWidth =
    width == null
      ? 0
      : (upload.sentBytes / upload.totalBytes) * (width - 16) + 16;

  return (
    <div
      className={cn('h-4 rounded-lg bg-gray-100 shadow-md', className)}
      ref={ref}
      {...props}
    >
      <div
        className="h-4 rounded-lg bg-blue-500 duration-100"
        style={{
          width: progressWidth,
        }}
      />
    </div>
  );
}

export function FailBar({
  className,
  ...props
}: HTMLAttributes<HTMLDivElement>) {
  return (
    <div
      className={cn(
        'h-4 rounded-lg border bg-red-700 text-center text-xs font-bold uppercase leading-tight text-white shadow-md',
        className
      )}
      {...props}
    />
  );
}

export function StatusBar(props: {
  children?: React.ReactNode;
  upload: Upload;
}) {
  const { children, upload } = props;

  switch (upload.status) {
    case 'progress': {
      return <ProgressBar upload={upload} />;
    }
    case 'error': {
      return <FailBar>Upload Failed</FailBar>;
    }
    case 'not-found': {
      return <FailBar>Uploading...</FailBar>;
    }
    case 'success': {
      return children || null;
    }
    default: {
      throw new Error(`Should be unreachable`);
    }
  }
}

'use client';

import React from 'react';

import type { PlateCloudEditor } from '@udecode/plate-cloud';

import { useEditorRef } from '@udecode/plate-common';

const buttonStyle: React.CSSProperties = {
  background: '#f0f0f0',
  border: 'none',
  cursor: 'pointer',
  marginRight: 4,
  padding: 8,
};

export function CloudToolbarButtons() {
  const editor = useEditorRef<PlateCloudEditor>();
  const getSaveValue = () => {
    console.info('editor.children', editor.children);
    console.info('editor.cloud.getSaveValue()', editor.cloud.getSaveValue());
  };

  const finishUploads = async () => {
    await editor.cloud.finishUploads();
  };

  return (
    <>
      <button onClick={getSaveValue} style={buttonStyle} type="button">
        Get Save Value
      </button>
      <button onClick={finishUploads} style={buttonStyle} type="button">
        Await Finish Uploads
      </button>
      <span>
        Note: After clicking a button, output will be shown in console.
      </span>
    </>
  );
}
