      defaultNodeValue: 'start',
      nodeKey: 'align',
      styleKey: 'textAlign',
      validNodeValues: ['start', 'left', 'center', 'right', 'end', 'justify'],
    },
    targetPlugins: [KEYS.p],
    // \u8FD9\u5C06\u6CE8\u5165\u5230\u6240\u6709\`targetPlugins\`\u4E2D\u3002\u672C\u4F8B\u4E2D\uFF0CParagraphPlugin\u5C06\u80FD\u591F\u53CD\u5E8F\u5217\u5316\`textAlign\`\u6837\u5F0F\u3002
