}).overrideEditor(({ editor, tf: { deleteForward }, api: { isInline } }) => ({
  // \u91CD\u5199\u8F6C\u6362
  transforms: {
    deleteForward(options) {
      // \u5220\u9664\u524D\u7684\u81EA\u5B9A\u4E49\u903B\u8F91
      console.info('\u5411\u524D\u5220\u9664\u4E2D...');

      // \u8C03\u7528\u539F\u59CB\u8F6C\u6362
      deleteForward(options);

      // \u5220\u9664\u540E\u7684\u81EA\u5B9A\u4E49\u903B\u8F91
      console.info('\u5DF2\u5411\u524D\u5220\u9664');
    },
  },
  // \u91CD\u5199API\u65B9\u6CD5
  api: {
    isInline(element) {
      // \u81EA\u5B9A\u4E49\u884C\u5185\u5143\u7D20\u68C0\u67E5
      if (element.type === 'custom-inline') {
        return true;
      }

      // \u56DE\u9000\u5230\u539F\u59CB\u884C\u4E3A
      return isInline(element);
    },
  },
}));
