quoteElement),\\n    ],\\n    value: () => {\\n      const savedValue = localStorage.getItem(\\n        \`nextjs-plate-value-demo-\${new Date().toISOString().split('T')[0]}\`\\n      );\\n\\n      return savedValue ? JSON.parse(savedValue) : initialValue;\\n    },\\n  });\\n\\n  return (\\n    <Plate\\n      onChange={({ value }) => {\\n        localStorage.setItem(\\n          \`nextjs-plate-value-demo-\${new Date().toISOString().split('T')[0]}\`,\\n          JSON.stringify(value)\\n        );\\n      }}\\n      editor={editor}\\n    >\\n      <FixedToolbar className=\\"flex justify-start gap-1 rounded-t-lg\\">\\n        <ToolbarButton onClick={() => editor.tf.h1.toggle()}>H1</ToolbarButton>\\n        <ToolbarButton onClick={() => editor.tf.h2.toggle()}>H2</ToolbarButton>\\n        <ToolbarButton onClick={() => editor.tf.h3.toggle()}>H3</ToolbarButton>\\n\\n        <ToolbarButton onClick={() => editor.tf.blockquote.toggle()}>\\n          Quote\\n        </ToolbarButton>\\n\\n        <MarkToolbarButton nodeType=\\"bold\\" tooltip=\\"Bold (\u2318+B)\\">\\n          B\\n        </MarkToolbarButton>\\n        <MarkToolbarButton nodeType=\\"italic\\" tooltip=\\"Italic (\u2318+I)\\">\\n          I\\n        </MarkToolbarButton>\\n        <MarkToolbarButton nodeType=\\"underline\\" tooltip=\\"Underline (\u2318+U)\\">\\n          U\\n        </MarkToolbarButton>\\n\\n        <div className=\\"flex-1\\" />\\n\\n        <ToolbarButton\\n          className=\\"px-2\\"\\n          onClick={() => {\\n            editor.tf.setValue(initialValue);\\n          }}\\n        >\\n          Reset\\n        </ToolbarButton>\\n      </FixedToolbar>\\n\\n      <EditorContainer>\\n        <Editor placeholder=\\"Type your amazing content here...\\" />\\n      </EditorContainer>\\n    </Plate>\\n  );\\n}\\n","type":"registry:example","target":"components/installation-next-04-value-demo.tsx"},{"path":"../ui/editor.tsx","type":"registry:ui","target":"components/ui/editor.tsx"},{"path":"../ui/