`,(0,n.jsx)(l.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsx)(l.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { useForm } from 'react-hook-form';
import { Plate, PlateContent, usePlateEditor } from 'platejs/react';

type FormValues = {
  content: any;
};

export function EditorForm() {
  // 1. \u521B\u5EFA\u8868\u5355
  const form = useForm<FormValues>({
    defaultValues: {
      content: [
        { type: 'p', children: [{ text: '\u6765\u81EA shadcn/ui Form \u7684\u95EE\u5019\uFF01' }] },
      ],
    },
  });

  // 2. \u521B\u5EFA Plate \u7F16\u8F91\u5668
  const editor = usePlateEditor();

  const onSubmit = (data: FormValues) => {
    console.info('\u5DF2\u63D0\u4EA4\u6570\u636E:', data.content);
  };

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)}>
        <FormField
          control={form.control}
          name="content"
          render={({ field }) => (
            <FormItem>
              <FormLabel>\u5185\u5BB9</FormLabel>
              <FormControl>
                <Plate
                  editor={editor}
                  onChange={({ value }) => {
                    // \u540C\u6B65\u5230\u8868\u5355
                    field.onChange(value);
                  }}
                >
                  <PlateContent placeholder="\u8F93\u5165..." />
                </Plate>
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <button type="submit">\u63D0\u4EA4</button>
      </form>
    </Form>
  );
}
