`,(0,n.jsx)(l.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsx)(l.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`import { useForm } from 'react-hook-form';
import { Plate, PlateContent, usePlateEditor } from 'platejs/react';

function MyForm() {
  const form = useForm({
    defaultValues: {
      content: [
        { type: 'p', children: [{ text: '\u521D\u59CB\u5185\u5BB9...' }] },
      ],
    },
  });

  const editor = usePlateEditor();

  const onSubmit = (data) => {
    alert(JSON.stringify(data, null, 2));
  };

  return (
    <form onSubmit={form.handleSubmit(onSubmit)}>
      <Plate
        editor={editor}
        onChange={({ value }) => form.setValue('content', value)}
      >
        <PlateContent />
      </Plate>

      <button type="submit">\u4FDD\u5B58</button>

      <button
        type="button"
        onClick={() => {
          // \u91CD\u7F6E\u7F16\u8F91\u5668
          editor.tf.reset();
          // \u91CD\u7F6E\u8868\u5355
          form.reset();
        }}
      >
        \u91CD\u7F6E
      </button>
    </form>
  );
}
