"use client";

import * as React from "react";
import type { Value } from "platejs";
import { Plate, PlateContent, usePlateEditor } from "platejs/react";

/**
 * \u4E00\u4E2A\u53EF\u91CD\u7528\u7684\u7F16\u8F91\u5668\u7EC4\u4EF6\uFF0C\u5DE5\u4F5C\u65B9\u5F0F\u7C7B\u4F3C\u4E8E\u6807\u51C6\u7684 <Textarea>\uFF0C
 * \u63A5\u53D7 \`value\`\u3001\`onChange\` \u548C\u53EF\u9009\u7684 placeholder\u3002
 *
 * \u7528\u6CD5\uFF1A
 *
 * <FormField
 *   control={form.control}
 *   name="bio"
 *   render={({ field }) => (
 *     <FormItem>
 *       <FormLabel>\u4E2A\u4EBA\u7B80\u4ECB</FormLabel>
 *       <FormControl>
 *         <EditorField
 *           {...field}
 *           placeholder="\u544A\u8BC9\u6211\u4EEC\u4E00\u4E9B\u5173\u4E8E\u4F60\u81EA\u5DF1\u7684\u4FE1\u606F"
 *         />
 *       </FormControl>
 *       <FormDescription>\u4E00\u4E9B\u6709\u5E2E\u52A9\u7684\u63CF\u8FF0...</FormDescription>
 *       <FormMessage />
 *     </FormItem>
 *   )}
 * />
 */
export interface EditorFieldProps
  extends React.HTMLAttributes<HTMLDivElement> {
  /**
   * \u5F53\u524D\u7684 Plate \u503C\u3002\u5E94\u8BE5\u662F\u4E00\u4E2A Plate \u8282\u70B9\u6570\u7EC4\u3002
   */
  value?: Value;

  /**
   * \u5F53\u7F16\u8F91\u5668\u503C\u6539\u53D8\u65F6\u8C03\u7528\u3002
   */
  onChange?: (value: Value) => void;

  /**
   * \u7F16\u8F91\u5668\u4E3A\u7A7A\u65F6\u663E\u793A\u7684\u5360\u4F4D\u7B26\u6587\u672C\u3002
   */
  placeholder?: string;
}

export function EditorField({
  value,
  onChange,
  placeholder = "\u5728\u6B64\u8F93\u5165...",
  ...props
}: EditorFieldProps) {
  // \u4F7F\u7528\u63D0\u4F9B\u7684\u521D\u59CB \`value\` \u521B\u5EFA\u7F16\u8F91\u5668\u5B9E\u4F8B\u3002
  const editor = usePlateEditor({
    value: value ?? [
      { type: "p", children: [{ text: "" }] }, // \u9ED8\u8BA4\u7A7A\u6BB5\u843D
    ],
  });

  return (
    <Plate
      editor={editor}
      onChange={({ value }) => {
        // \u901A\u8FC7 onChange prop \u5C06\u66F4\u6539\u540C\u6B65\u56DE\u8C03\u7528\u8005
        onChange?.(value);
      }}
      {...props}
    >
      <PlateContent placeholder={placeholder} />
    </Plate>
  );
}
