`,(0,n.jsx)(l.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsx)(l.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`"use client";

import { z } from "zod";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";

import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { EditorField } from "./EditorField"; // \u5BFC\u5165\u4E0A\u9762\u7684\u7EC4\u4EF6

// 1. \u4F7F\u7528 zod \u5B9A\u4E49\u9A8C\u8BC1\u6A21\u5F0F
const FormSchema = z.object({
  bio: z
    .string()
    .min(10, { message: "\u4E2A\u4EBA\u7B80\u4ECB\u81F3\u5C11\u9700\u8981 10 \u4E2A\u5B57\u7B26\u3002" })
    .max(160, { message: "\u4E2A\u4EBA\u7B80\u4ECB\u4E0D\u80FD\u8D85\u8FC7 160 \u4E2A\u5B57\u7B26\u3002" }),
});

// 2. \u6784\u5EFA\u4E3B\u8868\u5355\u7EC4\u4EF6
export function EditorForm() {
  // 3. \u8BBE\u7F6E\u8868\u5355
  const form = useForm<z.infer<typeof FormSchema>>({
    resolver: zodResolver(FormSchema),
    defaultValues: {
      // \u8FD9\u91CC "bio" \u53EA\u662F\u4E00\u4E2A\u5B57\u7B26\u4E32\uFF0C\u4F46\u5982\u679C\u60A8\u5C06\u5176\u89E3\u6790\u4E3A JSON\uFF0C
      // \u6211\u4EEC\u7684\u7F16\u8F91\u5668\u4F1A\u5C06\u5176\u89E3\u91CA\u4E3A\u521D\u59CB\u5185\u5BB9
      bio: "",
    },
  });

  // 4. \u63D0\u4EA4\u5904\u7406\u51FD\u6570
  function onSubmit(data: z.infer<typeof FormSchema>) {
    alert("\u5DF2\u63D0\u4EA4: " + JSON.stringify(data, null, 2));
  }

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
        <FormField
          control={form.control}
          name="bio"
          render={({ field }) => (
            <FormItem>
              <FormLabel>\u4E2A\u4EBA\u7B80\u4ECB</FormLabel>
              <FormControl>
                <EditorField
                  {...field}
                  placeholder="\u544A\u8BC9\u6211\u4EEC\u4E00\u4E9B\u5173\u4E8E\u4F60\u81EA\u5DF1\u7684\u4FE1\u606F..."
                />
              </FormControl>
              <FormDescription>
                \u60A8\u53EF\u4EE5 <span>@\u63D0\u53CA</span> \u5176\u4ED6\u7528\u6237\u548C\u7EC4\u7EC7\u3002
              </FormDescription>
              <FormMessage />
            </FormItem>
          )}
        />
        <button type="submit" className="py-2 px-4 bg-primary text-white">
          \u63D0\u4EA4
        </button>
      </form>
    </Form>
  );
}
