.doc-body h1:not(:empty):before {
    counter-increment: heading;
    /* add 1 to heading */
    
    content: counter(heading) " ";
    width: 2em;
    display: inline-block;
}
.doc-body h1:not(:empty) {
    counter-reset: subheading section;
    /* set section to 0 */
}
.doc-body h2:not(:empty):before {
    content: counter(heading) "." counter(subheading) " ";
    counter-increment: subheading subsection;
    width: 2em;
    display: inline-block;
}
.doc-body h2:not(:empty) {
    counter-reset: section;
}
.doc-body h3:not(:empty):before {
    content: counter(heading) "." counter(subheading) "." counter(section) " ";
    counter-increment: section;
    width: 2em;
    display: inline-block;
}
.doc-body h3:not(:empty) {
    counter-reset: subsection;
}
